import React, { useState, useEffect, useRef } from 'react';
import { PageTitle, ErrorMessage, SuccessMessage } from '../../components/MainComponents'
import useAPI from '../../helpers/AmorinhaAPI';
import {Container, TextField, Button, FormGroup, Checkbox, FormControlLabel, Grid} from '@material-ui/core';
import { MuiPickersUtilsProvider, KeyboardDatePicker, } from '@material-ui/pickers';
import 'date-fns';
import DateFnsUtils from '@date-io/date-fns';
// import { useFormik } from 'formik';
import { useStyles } from './styled';



function Page(){

    const usestyles = useStyles();

    const api = useAPI();

    const fileField = useRef();

    const [id, setId] = useState('');
    const [name, setName] = useState('');
    const [birthDate, setBirthDate] = useState(new Date());
    const [responsableName, setResponsableName] = useState('');
    const [phone, setPhone] = useState('');
    const [emergencyWarning, setEmergencyWarning] = useState('');
    const [phoneEmergency, setPhoneEmergency] = useState('');
    const [foodRestriction, setFoodRestriction] = useState(false);
    const [descriptionFoodRestriction, setDescriptionFoodRestriction] = useState('');
    const [imageAuthorization, setImageAuthorization] = useState(false);
    const [authorizedPeople , setAuthorizedPeople] = useState('');
    const [classes, setClasses] = useState('');
    const [additionalNotes, setAdditionalNotes] = useState('');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [disable, setDisable] = useState(false);
    const [button, setButton] = useState('Cadastrar');

    const [classesList, setClassesList] = useState([]);

    useEffect(() => {
        const getClasses = async () => {
            const json = await api.getClasses();
            setClassesList(json.classes);
        };

        getClasses();
    }, [])

    const getStudent = async (id) => {
        const json = await api.getStudent(id);

        setId(id);
        setName(json.student.name);
        setBirthDate(json.student.birthDate);
        setResponsableName(json.student.responsableName);
        setPhone(json.student.phone);
        setEmergencyWarning(json.student.emergencyWarning);
        setFoodRestriction(json.student.foodRestriction);
        setDescriptionFoodRestriction(json.student.descriptionFoodRestriction);
        setImageAuthorization(json.student.imageAuthorization);
        setAuthorizedPeople(json.student.authorizedPeople);
        setClasses(json.student.classes);
        setAdditionalNotes(json.student.additionalNotes);
        setButton('Editar');

    };


    const handleSubmit = async (e) => {
        e.preventDefault();
        setDisable(true);
        setError('');

        console.log(name);
        alert(name);

        const fData = new FormData();
        fData.append('name', name);
        fData.append('birthDate', birthDate);
        fData.append('responsableName', responsableName);
        fData.append('phone', phone);
        fData.append('emergencyWarning', emergencyWarning);
        fData.append('phoneEmergency', phoneEmergency);
        fData.append('foodRestriction', foodRestriction);
        fData.append('descriptionFoodRestriction', descriptionFoodRestriction);
        fData.append('imageAuthorization', imageAuthorization);
        fData.append('authorizedPeople', authorizedPeople);
        fData.append('classes', classes);
        fData.append('additionalNotes', additionalNotes);

        if (fileField.current.files.length > 0){
            fData.append('name', fileField.current.files[0]);
        }

        if (id){
            fData.append('id', id);
        }

        const json = await api.addStudent(fData);

        if (json.error){
            setError(json.error);
        } else {
            setSuccess("Cadastro realizado com sucesso!");
            /**CRIAR UM BOTÃO EDITAR */
            // history.push(`/students/edit/${json.id}`);
            getStudent(json.id);
        }

        setDisable(false);
    }


    return(
        <Container>
            <div className={usestyles.root}>
            <Grid container spacing={3}>
        <Grid item xs={12}>
          <Paper className={classes.paper}>xs=12</Paper>
        </Grid>
        </Grid>
            </div>
            <PageTitle>Cadastro de Aluno</PageTitle>

                {error &&
                    <ErrorMessage>{error}</ErrorMessage>
                }
                {success &&
                    <SuccessMessage>{success}</SuccessMessage>
                }
                <FormGroup onSubmit={handleSubmit}>
                    <TextField fullWidth label="Nome" type="text" value={name} onChange={ e => setName(e.target.value) } // required
                    />            

                    <MuiPickersUtilsProvider utils={DateFnsUtils}>
                    <Grid container justifyContent="space-around">
                        <TextField fullWidth label="Nome" type="text" value={name} onChange={ e => setName(e.target.value) } />

                        <KeyboardDatePicker
                        disableToolbar
                        variant="inline"
                        format="dd/MM/yyyy"
                        margin="normal"
                        id="date-picker-inline"
                        label="Data de Nascimento"
                        value={birthDate}
                        onChange={ e => setBirthDate(e.target.value) }
                        KeyboardButtonProps={{
                            'aria-label': 'change date',
                        }}
                        />
                    </Grid>
                    </MuiPickersUtilsProvider>    

                    <TextField fullWidth type="date" label="Data de Nascimento" disabled={disable} value={birthDate} onChange={ e => setBirthDate(e.target.value) } // required
                    />

                    <TextField fullWidth type="text" label="Nome do Responsável" disabled={disable} value={responsableName} onChange={ e => setResponsableName(e.target.value) } // required 
                    />

                    <TextField fullWidth type="text" label="Telefone de Contato" disabled={disable} value={phone} onChange={ e => setPhone(e.target.value) } // required 
                    />

                    <TextField fullWidth type="text" label="Aviso de Emergência" disabled={disable} value={emergencyWarning} onChange={ e => setEmergencyWarning(e.target.value) } // required 
                    />

                    <TextField fullWidth type="text" label="Telefone para Emergência" disabled={disable} value={phoneEmergency} onChange={ e => setPhoneEmergency(e.target.value) } // required 
                    />

                    {/* <FormControlLabel label="Restrição Alimentar" control={<Checkbox disabled={disable} onChange={e => setFoodRestriction(!foodRestriction)} />} /> */}


                    <TextField fullWidth type="text" label="Descrição da Restrição Alimentar" disabled={disable} value={descriptionFoodRestriction} onChange={ e => setDescriptionFoodRestriction(e.target.value) } // required 
                    />

                    <FormControlLabel label="Autorização Para Uso da Imagem" control={<Checkbox disabled={disable} onChange={ e => setImageAuthorization(true)} /> } /> 


                    <TextField fullWidth type="text" label="Autorizados Para Buscar a Criança" disabled={disable} value={authorizedPeople} onChange={ e => setAuthorizedPeople(e.target.value)  } // required 
                    />

                    {/* <TextField fullWidth type="text" label="Turma" disabled={disable} value={classesList} onChange={ e => setClasses(e.target.value)  } // required 
                    /> */}

                    <TextField fullWidth type="text" label="Observações Adicionais" disabled={disable} value={additionalNotes} onChange={ e => setAdditionalNotes(e.target.value)  } // required 
                    />

                     {/* <TextField fullWidth type="file" label="Adicionar Foto" disabled={disable} value={fileField} onChange={ e => setAdditionalNotes(e.target.value)  } // required 
                    /> */}

                    <Button type="submit" variant="contained" color="primary" disabled={disable}>{button}</Button>


                </FormGroup>

        </Container>
    )
}

export default Page;
