import React, { useEffect, useState } from 'react';
import { PageArea } from './styled';
import { PageContainer, PageTitle, ErrorMessage } from '../../components/MainComponents'
import useAPI from '../../helpers/AmorinhaAPI';
import { doLogin } from '../../helpers/AuthHandler';

function Page(){

    const api = useAPI();

    const [name, setName] = useState('');
    const [federalState, setFederalState] = useState([]);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [disable, setDisable] = useState(false);
    const [error, setError] = useState('');

    useEffect(() => {
        const getStates = async () => {
            const federalStatesList = await api.getStates();
            setFederalState(federalStatesList);
        }
        getStates();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setDisable(true);
        setError('');

        if (password !== confirmPassword){
            setError("Senhas diferentes");
            return
        }

        const json = await api.register(name, email, password, federalState);

        if (json.error){
            setError(json.error);
            setDisable(false);
        } else {
            doLogin(json.token);
            window.location.href = '/';
        }

        setDisable(false);
    }

    return(
        <PageContainer>
            <PageTitle>Inscrever-se</PageTitle>
            <PageArea>
                {error &&
                    <ErrorMessage>{error}</ErrorMessage>
                }
                <form onSubmit={handleSubmit}>
                    <label className="area">
                        <div className="area-title">Nome</div>
                        <div className="area-input">
                            <input 
                                type="text" 
                                disabled={disable} 
                                value={name}
                                onChange={ e => setName(e.target.value) }
                                required
                            />
                        </div>
                    </label>

                    <label className="area">
                        <div className="area-title">Estado</div>
                        <div className="area-input">
                            <select value={federalState} onChange={e => setFederalState(e.target.value)} required>
                                {federalState.map( (i, k) => (
                                    <options key={k}>{i.state}</options>
                                ))}

                            </select>
                        </div>
                    </label>

                    <label className="area">
                        <div className="area-title">Email</div>
                        <div className="area-input">
                            <input 
                                type="email" 
                                disabled={disable} 
                                value={email}
                                onChange={ e => setEmail(e.target.value) }
                                required
                            />
                        </div>
                    </label>

                    <label className="area">
                        <div className="area-title">Senha</div>
                        <div className="area-input">
                            <input 
                                type="password" 
                                disabled={disable} 
                                value={password}
                                onChange={ e => setPassword(e.target.value) }
                                required
                            />
                        </div>
                    </label>

                    <label className="area">
                        <div className="area-title">Confirmar Senha</div>
                        <div className="area-input">
                            <input 
                                type="password" 
                                disabled={disable} 
                                value={confirmPassword}
                                onChange={ e => setConfirmPassword(e.target.value) }
                                required
                            />
                        </div>
                    </label>



                    <label className="area">
                        <div className="area-title"></div>
                        <div className="area-input">
                            <button disabled={disable}>Salvar Cadastro</button>
                        </div>
                    </label>
                </form>
            </PageArea>
        </PageContainer>
    )
}

export default Page;
