import React, { useState, useEffect, useRef } from 'react';
import { PageTitle, ErrorMessage, SuccessMessage } from '../../components/MainComponents'
import useAPI from '../../helpers/AmorinhaAPI';
import {Container, TextField, Button, FormGroup, Checkbox, FormControlLabel, Grid, Select, InputLabel, MenuItem} from '@material-ui/core';
import { MuiPickersUtilsProvider, KeyboardDatePicker, } from '@material-ui/pickers';
import 'date-fns';
import DateFnsUtils from '@date-io/date-fns';
// import { useFormik } from 'formik';
import { useStyles } from './styled';



function Page(){

    const usestyles = useStyles();

    const api = useAPI();

    const fileField = useRef();

    const [id, setId] = useState('');
    const [name, setName] = useState('');
    const [birthDate, setBirthDate] = useState(new Date());
    const [responsableName, setResponsableName] = useState('');
    const [phone, setPhone] = useState('');
    const [emergencyWarning, setEmergencyWarning] = useState('');
    const [phoneEmergency, setPhoneEmergency] = useState('');
    const [foodRestriction, setFoodRestriction] = useState(false);
    const [descriptionFoodRestriction, setDescriptionFoodRestriction] = useState('');
    const [imageAuthorization, setImageAuthorization] = useState(false);
    const [authorizedPeople , setAuthorizedPeople] = useState('');
    const [classes, setClasses] = useState('Turminha');
    const [additionalNotes, setAdditionalNotes] = useState('');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [disable, setDisable] = useState(false);
    const [button, setButton] = useState('Cadastrar');

    const [classesList, setClassesList] = useState([]);

    useEffect(() => {
        const getClasses = async () => {
            const json = await api.getClasses();
            setClassesList(json.classes);
        };

        getClasses();
    }, [])

    const getStudent = async (id) => {
        const json = await api.getStudent(id);

        setId(id);
        setName(json.student.name);
        setBirthDate(json.student.birthDate);
        setResponsableName(json.student.responsableName);
        setPhone(json.student.phone);
        setEmergencyWarning(json.student.emergencyWarning);
        setFoodRestriction(json.student.foodRestriction);
        setDescriptionFoodRestriction(json.student.descriptionFoodRestriction);
        setImageAuthorization(json.student.imageAuthorization);
        setAuthorizedPeople(json.student.authorizedPeople);
        setClasses(json.student.classes);
        setAdditionalNotes(json.student.additionalNotes);
        setButton('Editar');

    };


    const handleSubmit = async (e) => {
        e.preventDefault();
        setDisable(true);
        setError('');

        console.log(name);
        alert(name);

        const fData = new FormData();
        fData.append('name', name);
        fData.append('birthDate', birthDate);
        fData.append('responsableName', responsableName);
        fData.append('phone', phone);
        fData.append('emergencyWarning', emergencyWarning);
        fData.append('phoneEmergency', phoneEmergency);
        fData.append('foodRestriction', foodRestriction);
        fData.append('descriptionFoodRestriction', descriptionFoodRestriction);
        fData.append('imageAuthorization', imageAuthorization);
        fData.append('authorizedPeople', authorizedPeople);
        fData.append('classes', classes);
        fData.append('additionalNotes', additionalNotes);

        if (fileField.current.files.length > 0){
            fData.append('name', fileField.current.files[0]);
        }

        if (id){
            fData.append('id', id);
        }

        const json = await api.addStudent(fData);

        if (json.error){
            setError(json.error);
        } else {
            setSuccess("Cadastro realizado com sucesso!");
            /**CRIAR UM BOTÃƒO EDITAR */
            // history.push(`/students/edit/${json.id}`);
            getStudent(json.id);
        }

        setDisable(false);
    }

    console.log(classes);


    return(
        <Container>
            <div className={usestyles.root}>
                <Grid container spacing={3}>
                    <Grid item xs={12}>
                        <h3 className={usestyles.pagetitle}>Cadastro de Aluno</h3>
                    </Grid>
                </Grid>
            </div>


                {error &&
                    <ErrorMessage>{error}</ErrorMessage>
                }
                {success &&
                    <SuccessMessage>{success}</SuccessMessage>
                }
                <FormGroup onSubmit={handleSubmit}>
                    <div className={usestyles.root}>
                        <Grid container spacing={3}>
                            <Grid item xs={8}>
                                <TextField fullWidth label="Nome" type="text" value={name} onChange={ e => setName(e.target.value) } />
                            </Grid>
                            <MuiPickersUtilsProvider utils={DateFnsUtils}>
                                <Grid container justifyContent="space-around" item xs={4}>
                                    <KeyboardDatePicker
                                    disableToolbar
                                    variant="inline"
                                    format="dd/MM/yyyy"
                                    margin="normal"
                                    // id="date-picker-inline"
                                    label="Data de Nascimento"
                                    value={birthDate}
                                    onChange={ (date) => setBirthDate(date) }
                                    KeyboardButtonProps={{
                                        'aria-label': 'change date',
                                    }}
                                    />
                                </Grid>
                            </MuiPickersUtilsProvider>    

                            <Grid item xs={8}>
                                <TextField fullWidth type="text" label="Nome do ResponsÃ¡vel" disabled={disable} value={responsableName} onChange={ e => setResponsableName(e.target.value) } />
                            </Grid>

                            <Grid item xs={4}>
                                <TextField fullWidth type="text" label="Telefone de Contato" disabled={disable} value={phone} onChange={ e => setPhone(e.target.value) } />
                            </Grid>

                            <Grid item xs={8}>
                                <TextField fullWidth type="text" label="Aviso de EmergÃªncia" disabled={disable} value={emergencyWarning} onChange={ e => setEmergencyWarning(e.target.value) } />
                            </Grid>

                            <Grid item xs={4}>
                                <TextField fullWidth type="text" label="Telefone para EmergÃªncia" disabled={disable} value={phoneEmergency} onChange={ e => setPhoneEmergency(e.target.value) } />
                            </Grid>

                            <Grid item xs={4}>
                                <FormControlLabel label="RestriÃ§Ã£o Alimentar" control={<Checkbox disabled={disable} onChange={e => setFoodRestriction(!foodRestriction)} />} />
                            </Grid>

                            <Grid item xs={4}>
                                <FormControlLabel label="AutorizaÃ§Ã£o Para Uso da Imagem" control={<Checkbox disabled={disable} onChange={ e => setImageAuthorization(true)} /> } /> 
                            </Grid>

                            <Grid item xs={4}>
                                <InputLabel id="demo-mutiple-name-label">Turma</InputLabel>
                                    <Select
                                    className={usestyles.formSelect}
                                    value={classes}
                                    onChange={ e => setClasses(e.target.value)  }
                                    >
                                    {classesList.map((i) => (
                                        <MenuItem key={i._id} value={i._id} > {i.name}  </MenuItem>
                                    ))}
                                </Select>
                            </Grid>

                            <Grid item xs={12}>
                                <TextField fullWidth type="text" label="DescriÃ§Ã£o da RestriÃ§Ã£o Alimentar" disabled={disable} value={descriptionFoodRestriction} onChange={ e => setDescriptionFoodRestriction(e.target.value) } />
                            </Grid>

                            <Grid item xs={12}>
                                <TextField fullWidth type="text" label="Autorizados Para Buscar a CrianÃ§a" disabled={disable} value={authorizedPeople} onChange={ e => setAuthorizedPeople(e.target.value)  } />
                            </Grid>

                            <Grid item xs={12}>
                                <TextField fullWidth type="text" label="ObservaÃ§Ãµes Adicionais" disabled={disable} value={additionalNotes} onChange={ e => setAdditionalNotes(e.target.value)  } />
                            </Grid>

                            <Grid item xs={12}>
                                {/* <TextField fullWidth type="file" label="Adicionar Foto" disabled={disable} value={fileField} onChange={ e => setAdditionalNotes(e.target.value)  } /> */}
                            </Grid>

                            <Grid item xs={12}>
                                <Button type="submit" variant="contained" color="primary" disabled={disable}>{button}</Button>
                            </Grid>

                        </Grid>

                    </div> {/* usestyle.root */}














                </FormGroup>

        </Container>
    )
}

export default Page;
