import React, { useState } from 'react';
import { PageArea } from './styled';
import { PageContainer, PageTitle, ErrorMessage } from '../../components/MainComponents'
import useAPI from '../../helpers/AmorinhaAPI';


function Page(){

    const api = useAPI();

    const [name, setName] = useState('');
    const [birthDate, setBirthDate] = useState('');
    const [responsableName, setResponsableName] = useState('');
    const [phone, setPhone] = useState('');
    const [emergencyWarning, setEmergencyWarning] = useState('');
    const [phoneEmergency, setPhoneEmergency] = useState('');
    const [foodRestriction, setFoodRestriction] = useState('');
    const [descriptionFoodRestriction, setDescriptionFoodRestriction] = useState('');
    const [imageAuthorization, setImageAuthorization] = useState('');
    const [authorizedPeople , setAuthorizedPeople] = useState('');
    const [schoolClass, setSchoolClass] = useState('');
    const [additionalNotes, setAdditionalNotes] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setDisable(true);
        setError('');

        const json = await api.login(email, password);

        if (json.error){
            setError(json.error);
        } else {
            doLogin(json.token, stayloggued);
            window.location.href = '/';
        }

        setDisable(false);
    }

    return(
        <PageContainer>
            <PageTitle>Cadastro</PageTitle>
            <PageArea>
                {error &&
                    <ErrorMessage>{error}</ErrorMessage>
                }
                <form onSubmit={handleSubmit}>
                    <label className="area">
                        <div className="area-title">Nome</div>
                        <div className="area-input">
                            <input 
                                type="text" 
                                disabled={disable} 
                                value={name}
                                onChange={ e => setEmail(e.target.value) }
                                required
                            />
                        </div>
                    </label>
                    <label className="area">
                        <div className="area-title">Data de Nascimento</div>
                        <div className="area-input">
                            <input 
                                type="date" 
                                disabled={disable} 
                                value={password}
                                onChange={ e => setPassword(e.target.value) }
                                required
                            />
                        </div>
                    </label>
                    <label className="area">
                        <div className="area-title">Permanecer Logado?</div>
                        <div className="area-input">
                            <input 
                                type="checkbox" 
                                disabled={disable} 
                                checked={stayloggued}
                                onChange={() => setStaylogged(!stayloggued)}
                            />
                        </div>
                    </label>
                    <label className="area">
                        <div className="area-title"></div>
                        <div className="area-input">
                            <button disabled={disable}>Logar</button>
                        </div>
                    </label>
                </form>
            </PageArea>
        </PageContainer>
    )
}

export default Page;
