import styled from 'styled-components';

export const HeaderArea = styled.div`
    background-color: #fff;
    height: 60px;
    border-bottom:1px solid #ccc;

    a {
        text-decoration:none;
    }

    .container {
        max-width:1000px;
        margin:auto;
        display:flex;
    }

    .logo img{
        width:55px;
        flex:1;
        display:flex;
        align-items:center;
    }

    nav {
        float: right;
        padding-top:10px;
        padding-bottom:10px;

        ul, li {
            margin:0;
            padding:0;
            list-style:none;
        }

        ul {
            align-items:center;
            height:40px;
            display:flex;
            border:1px solid red;
        }

        li {
            margin-left:20px;
            margin-right:20px;

            a {
                color:#000;

                &:hover {
                    color: #999;
                }
            }
        }
    }
`;
