import styled from 'styled-components';

export const PageArea = styled.div`
    display:flex;
    margin-top:20px;

    .home-image {
        border:1px solid #ddd;
        max-width: 500px;
        flex:1; 

        img {
            width: 95%;
            // padding:20px 0;


        }
    }

    .home-text {
        background-color:#f20adc;
        flex:1;
    }

    .text1 {
        font-size: 40px;
        height: 70px;
        display:flex;
        justify-content:center;
        align-items:center;
        text-align:center;
        color:#999;
        font-size:14px;
        border-top:1px solid #ccc;
        margin-top: 20px;
    }
`;
