import React, { useState, useEffect } from 'react';
import { useParams  } from 'react-router-dom';
import { PageArea } from './styled';
import { PageContainer } from '../../components/MainComponents'
import useAPI from '../../helpers/AmorinhaAPI';
import img_student from './aluno.png';

function Page(){

    const api = useAPI();

    const { id } = useParams();

    const [student, setStudent] = useState([]);
    console.log(student);

    useEffect(() => {
        const getStudent = async () => {
            const json = await api.getStudent(id);
            setStudent(json.student);
        }
        getStudent();
    },[]);

    return(
        <>  
            <PageContainer>
                <PageArea>
                <div className="container-student">
                        {student.map((i,k) =>
                            <>
                                <div key={k} className="img_student">
                                    <img src={img_student} alt="aluno" />
                                </div>
                                <div className="data-student">
                                    <span>{i.name}</span><br/>
                                    <span>{i.responsableName}</span><br/>
                                    <span>{i.phone}</span><br/>
                                </div>
                            </>
                        )}
                    </div>
                </PageArea>
            </PageContainer>
        </>
    )
}

export default Page;
