import styled from 'styled-components';

export const PageArea = styled.div`
    display:flex;

    .home-image {
        border:1px solid #ddd;
        max-width: 500px;
        flex:1; 

        img {
            width: 95%;
            padding:20px 0;


        }
    }

    .home-text1 {
        justify-content:center;
        align-items:center;
        text_align:center;

        p {
            font-size: 40px;
        }
    }
`;
