import React, { useState, useEffect } from 'react';
import { Link, useLocation, useHistory  } from 'react-router-dom';
import { PageArea, SearchArea } from './styled';
import { PageContainer } from '../../components/MainComponents'
import useAPI from '../../helpers/AmorinhaAPI';

let timer;

function Page(){

    const api = useAPI();
    const history = useHistory();

    const useQueryString = () => {
        return new URLSearchParams( useLocation().search )
    }

    const query = useQueryString();

    const [students, setStudents] = useState([]);
    const [query_search, setSearch] = useState(query.get('query_search') != null ? query.get('query_search') : '');
    console.log(students);

    // const getStudents = async () => {
    //     const json = await api.searchStudent({
    //         sort: 'asc',
    //         limit: 9,
    //         offset:0,
    //         query_search
    //     });
    //     setStudents(json.student_array);
    // };

    useEffect(() =>{
        const getStudents = async () => {
            const json = await api.searchStudent({
                sort: 'asc',
                limit: 9,
                offset:0,
                query_search
            });
            setStudents(json.student_array);
        };
        getStudents()
    }, []);

    useEffect(() => {

        if (query_search){
            history.replace({
                search: `?query_search=${query_search}`
            });
        }

        if (timer){
            clearTimeout(timer);
        }

        // timer = setTimeout(getStudents, 3000);

    }, [query_search]);


    return(
        <>  
            <SearchArea>
                {students.map(i => {
                    {i.map}
                })}
                <PageContainer>
                    <div className="search-box">
                        <form method="GET">
                            <input 
                                type="text" 
                                name="query_search" 
                                value={query_search}
                                placeholder="Qual aluno vocÃª procura?"
                                onChange={ e => setSearch(e.target.value) }
                            />
                            <button>Pesquisar</button>
                        </form>

                    </div>
                    <div className="list-student">

                    </div>
                </PageContainer>    
            </SearchArea>
            <PageContainer>
                <PageArea>
                    <h5>Resultado</h5>
                    {students.map((i, k) =>{
                        <h6>{i.name}</h6>
                    })}
                </PageArea>
            </PageContainer>
        </>
    )
}

export default Page;
