import React, { useState, useEffect } from 'react';
import { useParams  } from 'react-router-dom';
import { PageArea } from './styled';
import { PageContainer } from '../../components/MainComponents'
import useAPI from '../../helpers/AmorinhaAPI';
import img_student from './aluno.png';

function Page(){

    const api = useAPI();

    const { id } = useParams();

    const [student, setStudent] = useState([]);

    useEffect(() => {
        const getStudent = async () => {
            const json = await api.getStudent(id);
            setStudent(json.student);
        }
        getStudent();
    },[]);

    return(
        <>  
            <PageContainer>
                <PageArea>
                <div className="container-student">
                    <div className="list-students">
                        <div className="img_student">
                            <img src={img_student} alt="aluno" />
                        </div>
                        <div className="data-student">
                            <span>{`Aluno: ${student.name}`}</span><br/>
                            <span>{`Responsável: ${student.responsableName}`}</span><br/>
                            <span>{`Telefone: ${student.phone}`}</span><br/>
                        </div>
                        <br/>
                    </div>
                    <div className="complement-data">
                        Data de Nascimento: <span>{student.birthstudent}</span><br/>
                        Aviso de Emergência: <span>{student.emergencyWarning}</span><br/>
                    </div>
                </div>
            </PageArea>
        </PageContainer>
        </>
    )
}

export default Page;

