import React, { useState, useEffect } from 'react';
import { PageArea, SearchArea } from './styled';
import { PageContainer } from '../../components/MainComponents'
import useAPI from '../../helpers/AmorinhaAPI';

function Page(){

    const api = useAPI();

    const [students, setStudents] = useState([]);
    const [search, setSearch] = useState('');

    useEffect(() => {
        const getStudents = async () => {
            const studentList = await api.getStudents();
            setStudents(studentList);
        }
    }, []);

    const hadleSearch = async (e) => {
        e.preventDefault();

        const json = await api.searchStudent(search);
    }

    return(
        <>
            <SearchArea>
                <PageContainer>
                    <div className="search-box">
                        <form method="GET" onSubmit={hadleSearch}>
                            <input 
                                type="text" 
                                name="name" 
                                value={search}
                                placeholder="Qual aluno vocÃª procura?"
                                onChange={ e => setSearch(e.target.value) }
                            />
                            <button>Pesquisar</button>
                        </form>

                    </div>
                    <div className="list-student">

                    </div>
                </PageContainer>    
            </SearchArea>
            <PageContainer>
                <PageArea>
                    Lista dos alunos
                </PageArea>
            </PageContainer>
        </>
    )
}

export default Page;
