import React, { useState, useEffect } from 'react';
import { useParams  } from 'react-router-dom';
import { PageArea } from './styled';
import { PageContainer } from '../../components/MainComponents'
import useAPI from '../../helpers/AmorinhaAPI';
import img_student from './aluno.png';

function Page(){

    const api = useAPI();

    const { id } = useParams();

    const [student, setStudent] = useState([]);

    useEffect(() => {
        const getStudent = async () => {
            const json = await api.getStudent(id);
            setStudent(json.student);
        }
        getStudent();
    },[]);

    return(
        <>  
            <PageContainer>
                <PageArea>
                <div className="container-student">
                    <div className="row">
                        <div className="col-md-12">
                            <img src={img_student} alt="aluno" />
                        </div>
                    </div>

                    <div className="row">
                        <div className="col-md-4 text-align-right">
                            <span>Aluno</span><br/>
                            <span>Responsável</span><br/>
                            <span>Telefone</span><br/>
                        </div>
                        <div className="col-md-8">
                            <span>{student.name}</span><br/>
                            <span>{student.responsableName}</span><br/>
                            <span>{student.phone}</span><br/>
                        </div>
                    </div>


                    <div className="list-students">
                        <div className="img_student">

                        </div>
                        <div className="data-student">
                            <div className="left-text">

                            </div>
                            <div className="right-text">

                            </div>
                        </div>
                        <br/>
                    </div>
                    <div className="complement-data">
                         <span>{`Data de Nascimento: ${student.birthstudent}`}</span><br/>
                         <span>{`Aviso de Emergência: ${student.emergencyWarning}`}</span><br/>
                         <span>{`Telefone De emergência: ${student.phoneEmergency}`}</span><br/>
                         <span>{`Restrição alimentar: ${student.foodRestriction}`}</span><br/>
                         <span>{`Descrição da Restrição: ${student.descriptionFoodRestriction}`}</span><br/>
                         <span>{`Autorização de uso da Imagem: ${student.imageAuthorization}`}</span><br/>
                         <span>{`Pessoas Autorizadas: ${student.authorizedPeople}`}</span><br/>
                         <span>{`Turma: ${student.schoolClass}`}</span><br/>
                         <span>{`Observações: ${student.additionalNotes}`}</span><br/>
                    </div>
                </div>
            </PageArea>
        </PageContainer>
        </>
    )
}

export default Page;

