import React, { useState, useEffect } from 'react';
import { useParams  } from 'react-router-dom';
import { PageArea } from './styled';
import { PageContainer } from '../../components/MainComponents'
import useAPI from '../../helpers/AmorinhaAPI';
import img_student from './aluno.png';

function Page(){

    const api = useAPI();

    const { id } = useParams();

    const [student, setStudent] = useState([]);

    useEffect(() => {
        const getStudent = async () => {
            const json = await api.getStudent(id);
            setStudent(json.student);
        }
        getStudent();
    },[]);

    return(
        <>  
            <PageContainer>
                <PageArea>
                <div className="container-student">
                    <div className="row">
                        <div className="col-md-12">
                            <img src={img_student} alt="aluno" />
                        </div>
                    </div>

                    <div className="row">
                        <div className="col-md-3 text-right">
                            <p>Aluno</p>
                            <p>Responsável</p>
                            <p>Telefone</p>
                        </div>
                        <div className="col-md-8">
                            <p>{student.name}</p>
                            <p>{student.responsableName}</p>
                            <p>{student.phone}</p>
                        </div>
                    </div>

                    <hr/>

                    <div className="row">
                        <div className="col-md-4">
                            <p>Data de Nascimento:</p>
                            <p>Aviso de Emergência: {student.emergencyWarning}</p>
                            <p>Telefone De emergência: {student.phoneEmergency}</p>
                            <p>Restrição alimentar: {student.foodRestriction}</p>
                            <p>Descrição da Restrição: {student.descriptionFoodRestriction}</p>
                            <p>Autorização de uso da Imagem: {student.imageAuthorization}</p>
                            <p>Pessoas Autorizadas: {student.authorizedPeople}</p>
                            <p>Turma: {student.schoolClass}</p>
                            <p>Observações: {student.additionalNotes}</p>
                        </div>
                        <div className="col-md-8">
                            <p>{student.birthstudent}</p>
                            <p>{student.emergencyWarning}</p>
                            <p>{student.phoneEmergency}</p>
                            <p>{student.foodRestriction}</p>
                            <p>{student.descriptionFoodRestriction}</p>
                            <p>{student.imageAuthorization}</p>
                            <p>{student.authorizedPeople}</p>
                            <p>{student.schoolClass}</p>
                            <p>{student.additionalNotes}</p>
                        </div>
                    </div>
                    <div className="complement-data">

                    </div>
                </div>
            </PageArea>
        </PageContainer>
        </>
    )
}

export default Page;

