import React, { useState, useEffect } from 'react';
import { Link, useLocation, useHistory  } from 'react-router-dom';
import { PageArea, SearchArea } from './styled';
import { PageContainer } from '../../components/MainComponents'
import useAPI from '../../helpers/AmorinhaAPI';

function Page(){

    const api = useAPI();

    const useQueryString = () => {
        return new URLSearchParams( useLocation().search )
    }
    const query = useQueryString();

    const [students, setStudents] = useState('jael M');
    const [query_search, setSearch] = useState(query.get('query_search') != null ? query.get('query_search') : '');

    const getStudents = async () => {
        const json = await api.searchStudent({
            sort: 'asc',
            limit: 9,
            offset:0,
            query_search
        });
    }

    useEffect(() => {
        let queryString = [];
        if (query_search){
            queryString.push(`query_search=${query_search}`);
        }

        getStudents();

    }, [query_search]);


    return(
        <>
            <SearchArea>
                <PageContainer>
                    <div className="search-box">
                        <form method="GET">
                            <input 
                                type="text" 
                                name="name" 
                                value={query_search}
                                placeholder="Qual aluno vocÃª procura?"
                                onChange={ e => setSearch(e.target.value) }
                            />
                            <button>Pesquisar</button>
                        </form>

                    </div>
                    <div className="list-student">

                    </div>
                </PageContainer>    
            </SearchArea>
            <PageContainer>
                <PageArea>
                    {students}
                </PageArea>
            </PageContainer>
        </>
    )
}

export default Page;
