import styled from 'styled-components';

export const HeaderArea = styled.div`
    background-color: #fff;
    height: 60px;
    border-bottom:1px solid #ccc;

    .container {
        max-width:1000px;
        margin:auto;
        display:flex;
    }

    .logo img{
        width:60px;
        flex:1;
        display:flex;
        align-items:center;
    }
`;
